	        function fncSetCommonCode() {
	            try {
	            
					/*
					* 공통코드 Combo
					* ex : 	cm_setCommonCode (공통쿼리ID, 조건배열, 셋팅할 ID, code/value표시여부, displayYn, callback함수)
					*/
					//var codeType = "sysTypeByRole";
					//cm_setCommonCode(codeType, [SS_roleId], codeType, false, false, fncEndCommonCode);		//권한ID에 해당하는 시스템 구분
					cm_setCommonCodeSync("sysTypeByUser", [SS_userId], "sysTypeByUser", false, false);			//사용자별 시스템구분 조회
					cm_setCommonCodeSync("commonByLargeAll", [0028], readN, false, false);							//퀵메뉴 용 
					
					/* 1. 시스템구분 으로 Image View 여부 체크 */
					var opYn	= "N";		//운영 View 여부
					var inYn		= "N";		//정보 View 여부
					var mnYn	= "N";		//관리 View 여부
					var sysType = "";		//시스템구분 우선순위 (0:운영, 1:정보, 2:관리)
					
					var dataLength = dlt_sysTypeByUser.getRowCount();  	//사용자별 시스템구분 조회 

					if ( dataLength > 0 ){
						opYn = dlt_sysTypeByUser.getCellData(0, "content1");
						inYn = dlt_sysTypeByUser.getCellData(0, "content2");
						mnYn = dlt_sysTypeByUser.getCellData(0, "content3");
						sysType = dlt_sysTypeByUser.getCellData(0, "content4");

						//운영 View
						if ( opYn == "Y" ){
							hd_group16.show("");
							if ( sysType == null || sysType == "" ){
								sysType = "0";
							} 						
						}
						
						//정보 View
						if ( inYn == "Y" ){
							hd_group17.show("");
							if ( sysType == null || sysType == "" ){
								sysType = "1";
							} 						
						}						

						//관리 View
						if ( mnYn == "Y" ){
							hd_group18.show("");
							if ( sysType == null || sysType == "" ){
								sysType = "2";
							} 
						}
				
						fncChangeSysType(sysType);		//업무변경 
					}					
						
	            } catch (e) {
					alert("fncSetCommonCode(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}
	        }
	        	        
