	        /**
				함수명: fncQuery
				설  명: 메뉴 목록 Select (전체메뉴)
				인  자: sysType (선택 시스템구분)
				리  턴: 
			 */
			function fncQuery(sysType){
	            try {
	            	gv_sysType = sysType;			//현재 선택 시스템구분 셋팅 	            

					var requestData = {};
					requestData.sysType	= sysType;			//시스템 구분 
					requestData.memberId = SS_userId;

                    //권한체계 적용
				    var action		= "selectAllMenuListNew";
					
					//기존메뉴
					//var action     = "selectAllMenuList"; 
					var callBack	= fncQuery_RESULT;
					cm_submitAjax( gv_systemPath, action, requestData, callBack, false );	//서버 ajax 통신
	            } catch (e) {
					alert("fncQuery(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}
			}

			function fncQuery_RESULT(resultData){
	            try {
	            	//cm_logPrint("resultdata=>\n"+JSON.stringify(resultData));	//결과로그 출력
					//console.log("resultdata=>\n"+JSON.stringify(resultData));
	            	
					var menuList = resultData.resultList;

					for (i=0; i < menuList.length; i++) {
						if (menuList[i].menuFlag == "1") menuList[i].menuNm = menuList[i].gMenuNm;
						if (menuList[i].menuFlag == "2") menuList[i].menuNm = menuList[i].mMenuNm;
						if (menuList[i].menuFlag == "3") menuList[i].menuNm = menuList[i].programNm;

						var gMenuId =  menuList[i].gMenuId || "00";
						var mMenuId =  menuList[i].mMenuId || "00";
						var programId =  menuList[i].programId || "00";

						menuList[i].menuId =gMenuId + mMenuId + programId;
					}

                    //side.xml dlt_menu
					$p.main().se_wframe.getWindow().dlt_menu.setJSON(menuList);

					//결과정보 Instance 영역에 셋팅 
					var srcNode = WebSquare.xml.findNode(WebSquare.json.JSON2XML(resultData), "object/resultList");
					WebSquare.ModelUtil.setInstanceNode( srcNode , "menu" , null , "replace" );		// menu/resultList/object....
					
					//메뉴 html 생성 
					fncCreateMenuHtml();
					
	            } catch (e) {
					alert("fncQuery_RESULT(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}				
			}	

	        /**
				함수명: fncCreateMenuHtml
				설  명: 메뉴 생성 HTML
				인  자: 
				리  턴: 
			 */
			function fncCreateMenuHtml(){
	            try {
	            
	            	gen_firstGenerator.removeAll();
	            	
					var oldMenuFlag = "1";		//기존 메뉴구분(1:대메뉴, 2:중메뉴, 3:프로그램)
					var gMenuCnt = 0;
					var mMenuFlag = 0;			//대메뉴에 해당하는 중메뉴 CNT 

					var nodes = WebSquare.ModelUtil.findInstanceNodes( "menu/resultList/object" );
					
					var tmpParentIdx = 0;
					
					for (var i=0; i<nodes.length; i++){
						var menuFlag	= WebSquare.xml.getValue( nodes[i] , "menuFlag" );		//메뉴구분(1:대메뉴, 2:중메뉴, 3:프로그램)
						var gMenuId		= WebSquare.xml.getValue( nodes[i] , "gMenuId" );		//대메뉴ID
						var gMenuNm		= WebSquare.xml.getValue( nodes[i] , "gMenuNm" );		//대메뉴명
						var mMenuId		= WebSquare.xml.getValue( nodes[i] , "mMenuId" );		//중메뉴ID
						var mMenuNm		= WebSquare.xml.getValue( nodes[i] , "mMenuNm" );		//중메뉴명
						var programId	= WebSquare.xml.getValue( nodes[i] , "programId" );		//프로그램ID
						var programNm	= WebSquare.xml.getValue( nodes[i] , "programNm" );		//프로그램명
						var url			= WebSquare.xml.getValue( nodes[i] , "url" );			//프로그램URL
						var mMenuCnt	= WebSquare.xml.getValue( nodes[i] , "mMenuCnt" );		//대메뉴에 해당하는 중메뉴 CNT
						var popYn		= WebSquare.xml.getValue( nodes[i] , "popYn" );			//프로그램 popup 여부
						var programCd	= WebSquare.xml.getValue( nodes[i] , "programCd" );		//프로그램코드

						/* 대메뉴 */
						if ( menuFlag == "1" ) {
	
							tmpParentIdx = gen_firstGenerator.insertChild();

							var firstMenuLabel = gen_firstGenerator.getChild(tmpParentIdx, "btn_menu1_Label");
							
							firstMenuLabel.setValue(gMenuNm); // headerData_obj.TRX_CD_NM
							firstMenuLabel.setUserData("ins_webPath", url);
							firstMenuLabel.setUserData("ins_label", gMenuNm);
							firstMenuLabel.setUserData("ins_code", gMenuId);

						}
						
					} //for 문 끝

					// side.xml 메뉴 세팅
					fncInitMenu();


	            } catch (e) {
					alert("fncCreateMenuHtml(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}					
			}

	        /**
				함수명: fncChangeSysType
				설  명: 업무변경 선택 시 
				인  자: 
				리  턴: 
