			function fncChangeSysType(sysType){
	            try {

					// 이전 시스템의 windowContainer nameLayer 삭제;
					win_main_closeAll.click();
					
					/* 2. 시스템구분 우선순위로 Image src 변경 */
					if ( sysType == "0" ){
						hd_image1.setSrc( "/images/logo_nibs.png");

						sys_name.setValue("운영시스템");

						hd_image2.setSrc( "/images/btn_sys01_on.gif" );
						hd_image3.setSrc( "/images/btn_sys02_off.gif" );
						hd_image4.setSrc( "/images/btn_sys03_off.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '10'");
						
					} else if ( sysType == "1" ){
//						hd_image1.setSrc( "/images/logo_h1_02.gif");
						hd_image1.setSrc( "/images/logo_nibs.png");
						
						sys_name.setValue("정보시스템");

						hd_image2.setSrc( "/images/btn_sys01_off.gif" );
						hd_image3.setSrc( "/images/btn_sys02_on.gif" );
						hd_image4.setSrc( "/images/btn_sys03_off.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '35'");
						
					} else if ( sysType == "2" ){
//						hd_image1.setSrc( "/images/logo_h1_03.gif");
						hd_image1.setSrc( "/images/logo_nibs.png");
						sys_name.setValue("관리시스템");
						hd_image2.setSrc( "/images/btn_sys01_off.gif" );
						hd_image3.setSrc( "/images/btn_sys02_off.gif" );
						hd_image4.setSrc( "/images/btn_sys03_on.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '80'");
						
					} else {
						hd_image1.setSrc( "/images/logo_nibs.png");
					
						hd_image2.setSrc( "/images/btn_sys01_on.gif" );
						hd_image3.setSrc( "/images/btn_sys02_off.gif" );
						hd_image4.setSrc( "/images/btn_sys03_off.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '10'");
					}

					//최근 history 삭제;
					gen_history.removeAll();

					//메뉴 hidden 처리;
					group14.setStyle("display", "none");

					if ( sysType == "0" ) { 
						anchor1.setValue("운영시스템");
					} else if ( sysType == "1" ) { 
						anchor1.setValue("정보시스템");
					} else if ( sysType == "2" ) { 
						anchor1.setValue("관리시스템");
					}

					fncQuery(sysType);			//메뉴 목록 Select (전체메뉴)
					
	            } catch (e) {
					alert("fncChangeSysType(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}
			}

			scwin.fncChangeSystem = function (info) {
				var sysType = info.newValue;

				// 이전 시스템의 windowContainer nameLayer 삭제;
				win_main_closeAll.click();

				if ( sysType == "0" ){
						hd_image1.setSrc( "/images/logo_nibs.png");
					
						hd_image2.setSrc( "/images/btn_sys01_on.gif" );
						hd_image3.setSrc( "/images/btn_sys02_off.gif" );
						hd_image4.setSrc( "/images/btn_sys03_off.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '10'");
						
					} else if ( sysType == "1" ){
						hd_image1.setSrc( "/images/logo_h1_02.gif");
						
						hd_image2.setSrc( "/images/btn_sys01_off.gif" );
						hd_image3.setSrc( "/images/btn_sys02_on.gif" );
						hd_image4.setSrc( "/images/btn_sys03_off.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '35'");
						
					} else if ( sysType == "2" ){
						hd_image1.setSrc( "/images/logo_h1_03.gif");
					
						hd_image2.setSrc( "/images/btn_sys01_off.gif" );
						hd_image3.setSrc( "/images/btn_sys02_off.gif" );
						hd_image4.setSrc( "/images/btn_sys03_on.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '80'");
						
					} else {
						hd_image1.setSrc( "/images/logo_nibs.png");
					
						hd_image2.setSrc( "/images/btn_sys01_on.gif" );
						hd_image3.setSrc( "/images/btn_sys02_off.gif" );
						hd_image4.setSrc( "/images/btn_sys03_off.gif" );

						$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '10'");
					}

					//최근 history 삭제;
					gen_history.removeAll();

					group14.setStyle("display", "none");

					fncQuery(sysType);			//메뉴 목록 Select (전체메뉴)
			}
	        
