	        function fncQuickQuery_RESULT(resultData){
	            try {
	            	//cm_logPrint("resultdata=>"+JSON.stringify(resultData));	//결과로그 출력	            	
	            	var result = resultData.resultInfo;
            		var startTime	= cm_safeString(result.startTime);
            		var sTime		= cm_safeString(result.sTime);
            		var fTime		= cm_safeString(result.fTime);
				
					//퀵메뉴 View 설정 (출퇴근)
					if ( SS_deptCode == "20" && SS_officeCode == "10" ){
						//출퇴근용
						if ( startTime != "" && sTime == "" && fTime == "" ){
							gv_showGB = "Y";
							grp_quick_10.show( "" );
							grp_quick_11.show( "" );
						}
						//출근만
						if ( startTime != "" && sTime == "" && fTime != "" ){
							grp_quick_10.show( "" );
							grp_quick_11.hide();						
						}
						//퇴근만
						if ( startTime != "" && sTime != "" && fTime == "" ){
							grp_quick_10.hide();
							grp_quick_11.show( "" );
						}
						//출퇴근완료후
						if ( startTime == "" && sTime != "" && fTime != "" ){
							grp_quick_10.hide();
							grp_quick_11.hide();
						}	
					} else {
						grp_quick_10.hide();
						grp_quick_11.hide();
					}			
	            } catch (e) {
					alert("fncQuickQuery_RESULT(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}
	        }

