	        /**
				함수명: fncSetWorkTime
				설  명: 출/퇴근 정보 Update 
				인  자: gubun (출근:0, 퇴근:1)
				리  턴: 
			 */
			function fncSetWorkTime(gubun){
	            try {
					if(gubun == '0'){
						gv_showGB = "";
					}
					if(gubun == '1' && gv_showGB == 'Y'){
						alert(cm_getMessage("SYS.E0002"));		//출근처리 후 퇴근 처리 가능합니다.
						return;
					}
					
					var action		= "updateStartWorkTime";		//출근처리
					if(gubun == '1'){
						action		= "updateFinishWorkTime";		//퇴근처리
					}
					var requestData = {};
					var callBack	= fncSetWorkTime_RESULT;
					cm_submitAjax(gv_systemPath, action, requestData, callBack);	//서버 ajax 통신
											
	            } catch (e) {
					alert("fncSetWorkTime(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}
			}

			function fncSetWorkTime_RESULT(resultData){
	            try {
	            	//cm_logPrint("resultdata=>\n"+JSON.stringify(resultData));	//결과로그 출력	            	
	            	var gubun = resultData.gubun;
	            	if ( gubun == "0" ){
	            		alert(cm_getMessage("I0031","출근"));		//{0} 처리 되었습니다.
	            		//출근hide, 퇴근show
	            		grp_quick_10.hide();
	            		grp_quick_11.show( "" );
	            		
	            	} else if ( gubun == "1" ){	            		
	            		alert(cm_getMessage("I0031","퇴근"));		//{0} 처리 되었습니다.
	            		//출근hide, 퇴근hide
	            		grp_quick_10.hide();
	            		grp_quick_11.hide();	    
	            	}
	            } catch (e) {
					alert("fncSetWorkTime_RESULT(): " + e.message);
					WebSquare.exception.printStackTrace(e);
				}				
			}

