			function fncSaveCtiStatus(TerminalName, AgentState, UserID, ReasonCode, LoginID){
				try {
				//gv_sysType = sysType;     //현재 선택 시스템구분 셋팅              
					
				var action    = "saveCtiStatus";    //cti 상태 저장
				
