				requestData.memberId = SS_userId;       //아이디
				requestData.status = AgentState;        //cti 상태
				requestData.inTelNo = TerminalName;     //내선번호
				requestData.ctiId = UserID;             //cti id
				requestData.reasonCd = ReasonCode;      //원인코드
				
				var callBack  = fncSaveCtiStatus_RESULT;
				cm_submitAjax(gv_systemPath, action, requestData, callBack);  //서버 ajax 통신
				
				} catch (e) {
				alert("fncSaveCtiStatus(): " + e.message);
				WebSquare.exception.printStackTrace(e);
				}
			}

