			scwin.btn_menu1_Label_onclick = function () {
				//dlt_menu 갱신 처리;
				fncQuery(gv_sysType);

				var pageCode = this.getUserData("ins_code");
				$p.main().se_wframe.getWindow().trv_menu.setCondition("filter","gMenuId == '" + pageCode + "'");

				fncInitMenu();
				
				var i;
				var cmpChild;
				var numIndex;
				var cmpLabel;
				var genLen = gen_firstGenerator.getLength();

				numIndex = this.getGeneratedIndex();

				for ( i=0; i < genLen; i++ ) {
					cmpChild = gen_firstGenerator.getChild(i, "checked1DepthMenu");
					cmpLabel = gen_firstGenerator.getChild(i, "btn_menu1_Label");

					if ( numIndex == i ) {
						cmpChild.addClass("on");
						menuTitle.setValue(cmpLabel.getValue());
					} else {
						cmpChild.removeClass("on");
					}
				}
			};

			scwin.setMenuToggle = function() {
				if ( se_wframe.getStyle("width") > "100px" ) {
					$("#se_wframe").css("width", "0px");
					CONTAINER.setStyle("left", "-220px");
					$("#CONTAINER").css("transition", "all .5s linear");
				} else {
					$("#se_wframe").css("width", "220px");
					CONTAINER.setStyle("left", "0px");
					$("#CONTAINER").css("transition", "all .5s linear");
				}
			};

